package Day2;

public class Pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i, j, k=1;     
		 for(i=0; i<3; i++)      
		 {           
		 for(j=0; j<k; j++)          
		 {              
		  System.out.print("* ");          
		 }
		 k = k + 1;
		 System.out.println(); 
		 }

	}

}
